const smc =  require('./index'); // simple mongoose creator


// turorial 01
let collection_name = 'users';
let object_schema = {
    name: {
        type: String,
        trim: true,
        required: [true, 'The email is required']
    },
    password: {
        type: String,
        required: [true, 'The password is required']
    }
};
smc.smc(collection_name, object_schema);



